class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));

    for (int j = 0; j <= nums[0]; ++j) {
        int k = nums[0] - j;
        dp[0][j][k] = 1;
    }

    for (int i = 1; i < n; ++i) {
        for (int j = 0; j <= nums[i]; ++j) {
            int k = nums[i] - j;
            for (int pj = 0; pj <= j; ++pj) {   
                int pk = nums[i-1] - pj;
                if (k <= pk) {  
                    dp[i][j][k] = (dp[i][j][k] + dp[i-1][pj][pk]) % MOD;
                }
            }
        }
    }

    int result = 0;
    for (int j = 0; j <= nums[n-1]; ++j) {
        int k = nums[n-1] - j;
        result = (result + dp[n-1][j][k]) % MOD;
    }

    return result;
    }
};
