class Solution {
    private static final int MOD = 1_000_000_007;
    private int[][] memo;

    private int countPairs(int i, int prev, int[] nums) {
        if (i == nums.length) {
            return 1;
        }
        if (memo[i][prev] != -1) {
            return memo[i][prev];
        }

        int count = 0;
        for (int arr1_i = prev; arr1_i <= nums[i]; arr1_i++) {
            int arr2_i = nums[i] - arr1_i;

            if (i == 0 || arr2_i <= nums[i - 1] - prev) {
                count = (count + countPairs(i + 1, arr1_i, nums)) % MOD;
            }
        }

        memo[i][prev] = count;
        return count;
    }

    public int countOfPairs(int[] nums) {
        memo = new int[nums.length][51]; 
        for (int[] row : memo) {
            Arrays.fill(row, -1);
        }

        return countPairs(0, 0, nums);
    }
}
