class Solution:
    def countOfPairs(self, nums: List[int]) -> int:


        @cache
        def mono(i,a1s,a2s):
            nonlocal nums
            if i == len(nums):
                return 1
            total = 0
            for c in range(0,nums[i]+1):
                if c >= a1s and nums[i]-c <= a2s:
                    total += mono(i+1,c,nums[i]-c)
                    total = total%(10**9 + 7)
            return total%(10**9 + 7)

        return mono(0,-1,float('inf'))
