class Solution {
private:
    int solve(int ind,int prevl,int prevr,vector<int>& nums,vector<vector<vector<int>>> &dp)
    {
        if(ind ==nums.size())
        {
            return 1;
        }
        if(dp[ind][prevl][prevr]!=-1)
        {
            return dp[ind][prevl][prevr];
        }
        int res =0;
        for (int i = prevl;i<=nums[ind];i++)
        {
            if(nums[ind]-i<=prevr)
            {
                res = (res + solve(ind+1,i,nums[ind]-i,nums,dp))%1000000007;

            }

        }
        return dp[ind][prevl][prevr] = res;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp
        (n,vector<vector<int>>(52,vector<int>(52,-1)));

        return solve(0,0,51,nums,dp);

    }
};
