class Solution {
    public int countOfPairs(int[] nums) {
        int[] dp = new int[51];
        for(int idx = nums.length;idx>=0;idx--){
            int[] temp = new int[51];
            for(int a=0;a<=50;a++){
                if(idx == nums.length)temp[a] = 1;
                else{
                    int b = idx == 0 ? 50 : nums[idx-1] - a;
                    int ans = 0;
                    int mod = 1000000007;
                    for(int i=0;i<=nums[idx];i++){
                        if(i>=a && nums[idx]-i <= b){
                            ans += dp[i];
                            ans %= mod;
                        }
                    }
                    temp[a] = ans;   
                }
            }
            dp = temp;
        }
        return dp[0];
        // return solve(nums,0,0);
    }

