class Solution {
    public int countOfPairs(int[] nums) {
        int mod = 1_000_000_007;
        int n = nums.length;
        int result = 0;
        int[][] m = new int[n][51];

        for (int i = 0; i < n; i ++) {
            for (int j = 0; j <= nums[i]; j ++) {
                if (i == 0) {
                    m[i][j] = 1;
                } else {
                    int prevTotal = nums[i - 1];
                    for (int k = 0; k <= prevTotal; k ++) {
                        if (k <= j && prevTotal - k >= nums[i] - j) {
                            m[i][j] = (m[i - 1][k] + m[i][j]) % mod;
                        }
                    }
                }
            }
        }
        for (int i = 0; i <= 50; i ++) {
            result = (result + m[n - 1][i]) % mod;
        }
        return result;
    }
}
