class Solution {
public:

    int dp[2001][52][52];
    int mod = 1e9+7;

    int solve(int idx, int pre_ascen, int pre_decen, vector<int>& nums){
        if(idx==nums.size()) return 1;
        if(dp[idx][pre_ascen][pre_decen]!=-1) return dp[idx][pre_ascen][pre_decen];
        int ans=0;
        int num = nums[idx];
        for(int i=0; i<=num; i++){
            if(i>=pre_ascen && (num-i)<=pre_decen){
                ans =  (ans + (solve(idx+1,i,num-i,nums))%mod)%mod;
            }
        }
        return dp[idx][pre_ascen][pre_decen] = (ans)%mod;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(0,0,51,nums);

    }
};
