class Solution {
public:
    long long solve(vector<vector<vector<long long>>> &dp, vector<int>& nums, int ind, int mini, int maxi){
        if(ind == nums.size()) return 1;
        if(dp[ind][mini][maxi] != -1) return dp[ind][mini][maxi];

        long long count = 0;
        long long mod = 1e9+7;
        for(int val1=mini;val1<=nums[ind];val1++){
            int val2 = nums[ind] - val1;
            if(val1 >= mini && val2 <= maxi){
                count += (solve(dp,nums,ind+1,val1, val2)%mod);
                count %= mod;
            }
        }

        return dp[ind][mini][maxi] = count;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n + 1, vector<vector<long long>>(55, vector<long long>(55, -1)));
        return solve(dp, nums, 0, 0, 51);
    }
};
