class Solution {
public:

const int mod = 1e9 + 7;
    int solve(int i,int prev1,int prev2,vector<int>&nums,vector<vector<vector<int>>>&dp)
    {
        if(i==nums.size()) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int ans = 0;
        for(int k=prev1;k<=nums[i];k++){
             int rem = nums[i] - k;
             if(prev2>=rem){
                  ans = (ans + solve(i+1,k,rem,nums,dp))%mod;
             }
        }
        return dp[i][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
         vector<vector<vector<int>>>dp(nums.size(),vector<vector<int>>(51,vector<int>(51,-1)));
         return solve(0,0,50,nums,dp);
    }
};
