class Solution {
public:
int mod = 1e9 + 7;
int ans = 0;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int pre1 = -1, pre2 = -1;
        vector<vector<vector<int>>>dp(n, vector<vector<int>>(55, vector<int>(55, -1)));
        return solve(0, nums, pre1, pre2,dp);
    }
    int solve(int ind , vector<int>&nums, int pre1, int pre2, 
    vector<vector<vector<int>>>&dp){
        if(ind == nums.size()){
            return 1;
        }   
        if(dp[ind][pre1 + 1][pre2 + 1]!= -1)return dp[ind][pre1+1][pre2+1];
        int cnt = 0;

        for(int a = 0; a <= nums[ind]; a++){
            if(pre1 == -1 || pre1 <= a){
                int b = nums[ind] - a;
                if(pre2 == -1 || pre2 >= b){
                    cnt = (cnt + solve(ind + 1, nums, a, b, dp)% mod) % mod;
                }
            }
        }
        return dp[ind][pre1 + 1][pre2+1] =  cnt;
    }
};
