#include <vector>
#include <iostream>

using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int mod = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(51, vector<int>(n + 1, 0));

        for (int k = 0; k <= 50; ++k) {
            dp[k][n] = 1;
        }

        for (int i = n - 1; i > 0; --i) {
            for (int prev = 0; prev <= 50; ++prev) {
                int res = 0;
                for (int k = prev; k <= nums[i]; ++k) {
                    if (nums[i] - k <= nums[i - 1] - prev) {
                        res += dp[k][i + 1];
                        res %= mod;
                    }
                }
                dp[prev][i] = res;
            }
        }

        int res = 0;
        for (int k = 0; k <= nums[0]; ++k) {
            res += dp[k][1];
            res %= mod;
        }

        return res;
    }
};
