class Solution {
public:
    int mod = 1e9+7;
    vector<vector<vector<int>>> dp;
    int solve(vector<int> &nums, int ind, int firstLast, int secondLast) {
        if (ind == nums.size()) {
            return 1;
        }
        if (dp[ind][firstLast][secondLast] != -1) return dp[ind][firstLast][secondLast]; 
        long long ans = 0;
        for (int i=min(secondLast, nums[ind]); i>=0; i--) {
            int secondEle = i;
            int firstEle = nums[ind]-i;
            if (firstEle >= firstLast) {
                ans = (ans + solve(nums, ind+1, firstEle, secondEle)) % mod;
            }
        }

        return dp[ind][firstLast][secondLast] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        dp = vector<vector<vector<int>>>(nums.size()+1, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, nums[0]);
    }
};
