class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        MOD = 10**9+7
        pre = [1 for a in range(nums[0] + 1)]

        for i in range(1, len(nums)):
            n = nums[i]
            p = nums[i-1]
            curr = [0 for a in range(n + 1)]
            for x in range(len(pre)):
                for y in range(len(curr)):
                    if (y >= x and (n-y) <= (p-x)):
                        curr[y] += pre[x]
            pre = [a % MOD for a in curr]
        return sum(pre) % MOD
