class Solution {
public:
int mod = 1e9+7;
long long dp(int i, int prev, vector<int>& nums, vector<vector<int>>& mem) {

    if (i==nums.size()) return 1;
    if (mem[i][prev] != -1) return mem[i][prev];

    long long ans = 0;
    for(int l=nums[i]; l>=prev; --l) {


        int r = nums[i]-l;
        if (i!=0 && r>nums[i-1]-prev) break;

        ans = (ans + dp(i+1,l,nums,mem))%mod;
    }
    return mem[i][prev] = ans;

}

int countOfPairs(vector<int>& nums) {

    int max_el = *max_element(begin(nums),end(nums));
    vector<vector<int>> mem(nums.size(),vector<int>(max_el+1,-1));

    return dp(0,0,nums,mem);
}
};
