class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        #dp[i][a] = # of valid mon seq of length i w/ max element 'a'
        dp = [ {} for j in range(len(nums))]

        print(dp[0])

        for i in range(len(nums) ):
            for a in range(1+nums[i]):
                b = nums[i] - a
                dp[i][(a,b)] = 0
                if i == 0:
                    dp[i][(a,b)] = 1
                    continue
                for c in range(1+nums[i-1]):
                    d = nums[i-1]-c
                    if a >= c and b <= d:
                        if (c,d) in dp[i-1]:
                            dp[i][(a,b)] += dp[i-1][(c,d)]
                dp[i][(a,b)] %= 10**9+7
            #print(dp[i])

        return sum(dp[-1].values()) % (10**9+7)
