class Solution {
public:

    int n,X;

    int countOfPairs(vector<int>& v) {
        n=v.size();
        X=1e9+7;
        int mx=0;
        for(int i=0; i<n; i++){mx=max(mx,v[i]);}
        vector<vector<int>>t(n,vector<int>(mx+1,-1));
        int s=0;
        for(int j=0; j<=v[0]; j++)
        {
            s=(0ll+s+solve(1,j,v[0]-j,v,t))%X;
        }
        return s;
    }

    int solve(int i, int pre, int diff, vector<int>&v, vector<vector<int>>&t)
    {
        if(i==n) return 1;
        if(t[i-1][pre]!=-1) return t[i-1][pre];
        int s=0;
        for(int j=pre; j<=v[i]; j++)
        {
            if(v[i]-j<=diff)
                s=(0ll+s+solve(i+1,j,v[i]-j,v,t))%X;
        }
        return t[i-1][pre]=s;
    }
};
