class Solution {
    int[][][] mem;
    int mod = 1000000007;

    public int dp(int[] nums, int idx, int ap, int bp) {
        if (idx >= nums.length)
            return 1;

        if (mem[idx][ap][bp] != -1)
            return mem[idx][ap][bp];

        int a = 0;

        for (int i = 0; i <= nums[idx]; i++) {
            if (ap <= i && bp >= nums[idx] - i)
                a = (a + dp(nums, idx + 1, i, nums[idx] - i)) % mod;
        }

        mem[idx][ap][bp] = a;
        return a;
    }

    public int countOfPairs(int[] nums) {
        mem = new int[nums.length][51][51];

        for (int i = 0; i < nums.length; i++) {
            for (int j = 0; j < 51; j++) {
                for (int k = 0; k < 51; k++) {
                    mem[i][j][k] = -1;
                }
            }
        }

        return dp(nums, 0, 0, 50);
    }
}
