class Solution {
public:
    int mod = 1e9 + 7;

    int fun(vector<int>& nums, vector<vector<vector<int>>>& dp, int i, int j, int k) {
        if (i == nums.size()) return 1;

        if (dp[i][j][k] != -1) return dp[i][j][k];

        int ans = 0;

        for (int idx = j; idx <= nums[i]; idx++) {
            int rem = nums[i] - idx;
            if (rem <= k) {
                ans += fun(nums, dp, i + 1, idx, rem);
                ans %= mod;
            }
        }
        return dp[i][j][k] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        int ans = fun(nums,dp,0,0,nums[0]);

        return ans;
    }
};
