class Solution {
    int mod = 1e9+7;
    int solve(vector<vector<vector<int>>>& dp, int idx, int prev1, int prev2, vector<int>& nums){
        if (idx == nums.size()){
            return 1;
        }
        if (dp[idx][prev1][prev2] != -1){
            return dp[idx][prev1][prev2];
        }
        int sum = nums[idx];
        int i = prev1;
        int j = sum - i;
        int ans = 0;
        while (i<51 && j<51 && i>=0 && j>=0){
            if (i>=prev1 && j<=prev2)   ans = (ans + (solve(dp, idx+1, i, j, nums)))%mod;
            i++;
            j--;
        }
        return dp[idx][prev1][prev2] = ans;

    }
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size(), vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(dp, 0, 0, 50, nums);
    }
};
