class Solution {
public:
    int dp[2001][54][54];
    int solve(int&n,vector<int>&nums,int&e,int start,int p1,int p2){
        if(start == n){return 1;}
        if(dp[start][p1+1][p2+1] != -1)return dp[start][p1+1][p2+1];
        int res = 0;
        for(int i=0;i<=nums[start];i++){
            if((p1 == -1 || p1 <= i) && (p2 == -1 || p2 >= nums[start]-i)){
                res += solve(n,nums,e,start+1,i,nums[start]-i);
                res %=e;
            }
        }
        return dp[start][p1+1][p2+1] = res;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof(dp));
        int e = 1e9+7;
        return solve(n,nums,e,0,-1,-1);

    }
};
