class Solution {
public:
    int dp[2001][51][2];
    int mod = 1e9 + 7;
    int helper(vector<int>&nums, int idx, int prev, bool start) {
        if(idx >= nums.size()){
            return 1;
        }

        if(dp[idx][prev][start] != -1) return dp[idx][prev][start];

        int ans = 0;

        if(!start) {
            for(int i = 0; i <= nums[idx]; ++i) {
                ans = (ans + helper(nums, idx + 1, i, true)) % mod;
            }
        } else {
            int prev2 = nums[idx - 1] - prev;
            for(int i = prev; i <= nums[idx]; ++i) {
                int currPrev2 = nums[idx] - i;
                if(currPrev2 <= prev2) {
                    ans = (ans + helper(nums, idx + 1, i, true)) % mod;
                }
            }
        }

        return dp[idx][prev][start] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return helper(nums, 0, 0, false);
    }
};
