    int solve(int ind, int arr1Prev, int arr2Prev, vector<int> &nums){
        if(ind == nums.size())
            return 1;

        if(dp[ind][arr1Prev][arr2Prev] != -1)
            return dp[ind][arr1Prev][arr2Prev];

        long long int res = 0;
        for(int i = arr1Prev; i <= nums[ind]; i++)
        {
            if((nums[ind] - i) > arr2Prev || (nums[ind] - i) < 0)
                continue;
            res += solve(ind + 1, i, nums[ind] - i, nums);
            res %= MOD;
        }
        return dp[ind][arr1Prev][arr2Prev] = res;
    }

public:
    int countOfPairs(vector<int>& nums) {
        long long int cnt = 0;
        vector<int> a1, a2;
        memset(dp, -1, sizeof(dp));
        // unordered_map<string, int> dp;
        for(int i = 0; i <= nums[0]; i++)
        {
            cnt += solve(1, i, nums[0] - i, nums);   
            cnt %= MOD;
        }
        return cnt;
    }
