class Solution {
public:
    int mod = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxVal = *max_element(nums.begin(), nums.end());
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(maxVal + 1, vector<int>(maxVal + 1, -1)));
        return solve(0, 0, maxVal, nums, n, dp);
    }

private:
    int solve(int j, int prev1, int prev2, vector<int>& nums, int n, vector<vector<vector<int>>>& dp) {
        if (j == n) return 1;

        if (dp[j][prev1][prev2] != -1) return dp[j][prev1][prev2];

        int res = 0;
        for (int i = 0; i <= nums[j]; ++i) {
            int arr1_j = i;
            int arr2_j = nums[j] - i;

            if (arr1_j >= prev1 && arr2_j <= prev2) {
                res = (res + solve(j + 1, arr1_j, arr2_j, nums, n, dp)) % mod;
            }
        }

        return dp[j][prev1][prev2] = res;
    }
};
