class Solution {
public:
    int dp[2001][51][51];
    const int pri = 1e9 +  7;
    int n;
    int solve(int i, int k,int l,  vector<int>&nums){
        if(i >= n){
            return 1;
        }
        if(dp[i][k][l] != -1){
            return dp[i][k][l];
        }
        int ans = 0;
        for(int j = 0; j <= 50; j++){
            if(k + j <= nums[i] && (nums[i] - j - k) <= l){
                ans = (ans + solve(i + 1, j + k, nums[i] - j - k,  nums) ) % pri;
            }
        }
        return dp[i][k][l] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1,sizeof(dp));
        n = nums.size();
        int ans = solve(0, 0, nums[0], nums);
        return ans;
    }
};
