class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9  + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n + 1, vector<long long>(51, 0));

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                for (int k = 0; k <= 50; k++) {
                    if ((nums[i] - j) >= (nums[i - 1] - k) && j <= k) {
                        dp[i][j] += dp[i - 1][k];
                        dp[i][j] %= mod;
                    }
                }
            }
        }
        long int ans = 0;

        for (int i = 0; i <= 50; i++) {
            ans += dp[n - 1][i];
            ans %= mod;
        }
        return ans;
    }
};
