class Solution {
vector<vector<vector<int>>> dp;
int MOD=1e9+7;
private:
    int f(const vector<int>& nums, int pos, int p1, int p2,int& n) {
        if (pos==n) {
            return 1;
        }

        if (dp[pos][p1][p2] != -1) {
            return dp[pos][p1][p2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[pos]; ++i) {
            int l = i;
            int h = nums[pos] - i;
            if (l >= p1 && h <= p2) {
                ans = (ans + f(nums, pos + 1, l, h,n)) % MOD;
            }
        }

        dp[pos][p1][p2] = ans;
        return ans;
    }
public:
    int countOfPairs(const vector<int>& nums) {
        int n = nums.size();
        dp.resize(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return f(nums, 0, 0, 50,n);
    }
};
