#define ll long long int 
class Solution {
public:
    ll help(int ind, int a, int b , vector<int>& nums ,  vector<vector<vector<ll>>> &dp){
        if (ind >= nums.size()) return 1;
            ll result = 0;
            ll mod = 1e9+7; 
            if (dp[ind][a][b] != -1) return dp[ind][a][b];
            for (int i = a; i <= nums[ind]; i++) {
                for (int j = b; j >= 0; j--) {
                    if (i + j == nums[ind]) {
                        result += help(ind + 1, i, j , nums , dp);
                        result = result % mod;
                    }
                }
            }
            return dp[ind][a][b] = result;
    }
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<ll>>> dp(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return help(0, 0, nums[0] , nums , dp);
    }
};
