class Solution {
public:
vector<vector<int>>dp;
int mod=1e9+7;
    int solve(vector<int>&nums,vector<int>&maxis, int i, int prev,int prev_mini){
        if(i==nums.size())
            return 1;
        if(dp[i][prev]!=-1){
            return dp[i][prev];
        }
        long long res=0;
        for(int val=prev;val<=maxis[i] && val<=nums[i];val++){
             int cur_min=nums[i]-val;
             if(cur_min<=prev_mini){
                res=(res+solve(nums,maxis,i+1,val,cur_min)%mod)%mod;
             }
        }
        return dp[i][prev]=res;
    }
    int countOfPairs(vector<int>& nums) {
        vector<int>maxis(nums);
        for(int i=nums.size()-2;i>=0;i--){
            maxis[i]=min(maxis[i+1],maxis[i]);
        }
        dp.resize(nums.size()+1,vector<int>(51,-1));
        return solve(nums,maxis,0,0,1e9);
    }
};
