class Solution {
public:
    int mod = 1e9+7;
    int solve(int i, int one, int two, vector<int>& nums,  unordered_map<int, unordered_map<int, unordered_map<int, int>>> &dp)
    {
        if(i==nums.size())
        return 1;

        if(dp[i][one].find(two)!=dp[i][one].end())
        {
            return dp[i][one][two];
        }
        int cnt=0;
        for(int j = one; j<=nums[i]; j++)
        {
            int k = nums[i]-j;
            if(k<=two)
            {
                cnt = (cnt + solve(i+1, j, k, nums, dp))%mod;
            }
        }
        return dp[i][one][two] = cnt%mod;
    }
    int countOfPairs(vector<int>& nums) {
        unordered_map<int, unordered_map<int, unordered_map<int, int>>> dp;
        return solve(0, 0, nums[0], nums, dp)%mod;
    }
};
