class Solution {
public:
int mod=1e9+7;
#define ll long long
    int helper(vector<int>&nums, int n,int ind, int prev,vector<vector<int>>&dp){
        if(ind==n){
            return 1;
        }
        if(dp[ind][prev]!=-1)return dp[ind][prev];
        //nums1[ind] >= prevMin && nums2[ind]<=prevMax
        ll ans=0;
        int num2=nums[ind-1]-prev;
        for(int i=prev;i<=nums[ind];i++){
            if(nums[ind]-i<=num2){
                // cout<<i<<" "<<nums[ind]-i<<"\n";
                ans+=helper(nums,n,ind+1,i,dp);
                ans%=mod;
            }
        }
        return dp[ind][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        //pass from 1st index.
        ll ans=0;
        int n=nums.size();
        vector<vector<int>>dp(n,vector<int>(51,-1));
        for(int i=0;i<=nums[0];i++){
            ans=(ans+helper(nums,n,1,i,dp))%mod;
        }
        return ans;
    }
};
