class Solution {
public:

    const int M = 1e9 + 7;

    vector<vector<vector<int>>> dp;

    int solve(vector<int>& v, int ind, int MAX1, int MIN2){
        if(ind == v.size()) return 1;

        if(dp[ind][MAX1 + 1][MIN2 + 1] != -1) return dp[ind][MAX1 + 1][MIN2 + 1];

        long long s = 0;

        if(MAX1 == -1){
            for(int i = 0; i <= v[ind]; i++){
                s += solve(v,ind + 1,i,v[ind] - i);
                s %= M;
            }
        }
        else{
            for(int i = MAX1; i <= v[ind]; i++){
                if((v[ind] - i) <= MIN2){
                    s += solve(v,ind + 1,i, v[ind] - i);
                    s %= M;
                }
            }
        }

        return dp[ind][MAX1 + 1][MIN2 + 1] = s;
    }

    int countOfPairs(vector<int>& v) {
        int n = v.size();
        dp.resize(n, vector<vector<int>> (55, vector<int> (55, -1)));
        return solve(v,0,-1,-1);
    }
};
