MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums):
        n = len(nums)
        dpp = [[[0] * 51 for _ in range(51)] for _ in range(n + 1)]

        for a1 in range(nums[0] + 1):
            a2 = nums[0] - a1
            dpp[1][a1][a2] = 1

        for i in range(2, n + 1):
            for a1 in range(nums[i-1] + 1):
                a2 = nums[i-1] - a1
                for prev_a1 in range(a1 + 1):
                    prev_a2 = nums[i-2] - prev_a1
                    if prev_a2 >= a2:
                        dpp[i][a1][a2] = (dpp[i][a1][a2] + dpp[i-1][prev_a1][prev_a2]) % MOD


        res = 0
        for a1 in range(51):
            for a2 in range(51):
                res = (res + dpp[n][a1][a2]) % MOD

        return res
