class Solution {
public:
    int mod = 1e9+7;
    int f(int ind,int l1,int l2,vector<int>& nums,int &ans,vector<vector<int>> &dp)
    {

        if(ind == nums.size())
        {
            return 1;
        }
        if(dp[ind][l1]!=-1)
        return dp[ind][l1];
        int temp = 0;
        for(int i=0;i<=nums[ind];i++)
        {
            int p1 = i;
            int p2 = nums[ind] - i;
            if(p1>=l1 && p2<=l2)
            {
                temp+=(f(ind+1,p1,p2,nums,ans,dp)%mod);
                temp = temp%mod;
            }
        }
        dp[ind][l1] = temp%mod;
        return dp[ind][l1];
    }
    int countOfPairs(vector<int>& nums) {
        int ans = 0;
        vector<vector<int>> dp(nums.size(),vector<int> (51,-1));
        int val = f(0,0,50,nums,ans,dp);
        val = val%mod;
        return val;
    }
};
