class Solution {
public:
    const int MOD = 1000000007;

    unordered_map<int,unordered_map<int,int>>mp;
    int check(int i, int prvl, int prvr, vector<int>& nums) {

        if (i >= nums.size()) {
            return 1;
        }

        if(mp[i].count(prvl))
        {
            return mp[i][prvl];
        }

        int ans = 0;
        for (int num = nums[i]; num >= 0; num--) {
            // cout<<i<<" ";
            // cout<<num<<" "<<prvl<<" & "<<nums[i]-num<<" "<<prvr<<endl;
            if (num >= prvl && nums[i] - num <= prvr) {
                int ans1 = check(i + 1, num, nums[i] - num, nums);
                // cout << ans1 << " for " << i << " " << prvl << endl;
                ans = (ans + ans1) % MOD;
            } else {
                // cout<<"break "<<nums[i]<<" "<<num<<" "<<prvl<<endl;
                break;
            }
        }

        return mp[i][prvl]=ans;
    }

    int countOfPairs(vector<int>& nums) { return check(0, -1, 51, nums); }
};
