class Solution {
public:
    vector<int> nums;
    int MOD = 1e9 + 7;
    vector<vector<vector<int>>> tbl;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int offset = 50;

        tbl.resize(n + 1, vector<vector<int>>(51, vector<int>(101, -1)));

        this->nums = nums;
        return f1(0, 0, -1 + offset);
    }

    int f1(int i, int p1, int p2) {
        if (tbl[i][p1][p2] != -1) return tbl[i][p1][p2];
        if (i == nums.size()) return 1;

        int count = 0;
        for (int x1 = p1; x1 <= nums[i]; x1++) {
            int x2 = nums[i] - x1;
            int adjustedP2 = x2 + 50;

            if (p2 == 49) {
                count = (count + f1(i + 1, x1, adjustedP2)) % MOD;
            } else {
                if (x2 <= p2 - 50) {
                    count = (count + f1(i + 1, x1, adjustedP2)) % MOD;
                }
            }
        }

        return tbl[i][p1][p2] = count;
    }
};
