class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        const int BASE = 1000000007;

        const int n = nums.size();

        vector<array<int, 51>> memo(n, array<int, 51>());

        for (int num = 0; num <= nums[0]; num++) {
            memo[0][num] = 1;
        }

        for (int i = 0; i < n-1; i++) {

            for (int p1 = 0; p1 < 51; p1++) {
                if (memo[i][p1] == 0) continue;
                const int p2 = nums[i]-p1;

                for (int q1 = max(p1, nums[i+1] - p2); q1 <= nums[i+1]; q1++) {
                    // cout << q1 << endl;
                    memo[i+1][q1] += memo[i][p1];
                    memo[i+1][q1] %= BASE;
                }
            }

        }

        int res = 0;
        for (int p1 = 0; p1 < 51; p1++) {
            res += memo[n-1][p1];
            res %= BASE;
        }

        return res;

    }
};
