#define MOD 1000000007
#define MAX_VAL 50

typedef vector<int> IntArray;
typedef vector<vector<vector<int>>> DPTable;

auto init = []() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);
    return 'c';
}();

class Solution {
public:
    int countOfPairs(IntArray& nums) {
        int size = nums.size();
        dp.assign(size, vector<vector<int>>(MAX_VAL + 1, vector<int>(MAX_VAL + 1, -1)));
        return calculatePairs(nums, 0, 0, MAX_VAL);
    }

private:
    DPTable dp;

    int calculatePairs(const IntArray& nums, int index, int prev1, int prev2) {
        if (index >= nums.size()) {
            return 1;
        }

        if (dp[index][prev1][prev2] != -1) {
            return dp[index][prev1][prev2];
        }

        int result = 0;
        for (int value = 0; value <= nums[index]; ++value) {
            int firstValue = value;
            int secondValue = nums[index] - value;
            if (firstValue >= prev1 && secondValue <= prev2) {
                result = (result + calculatePairs(nums, index + 1, firstValue, secondValue)) % MOD;
            }
        }

        dp[index][prev1][prev2] = result;
        return result;
    }
};
