#define forz(i,a,b) for(int i = a;i<b;i++)
#define N 2001
#define mod 1000000007

typedef long long ll;

ll dp[N][51]; // dp[i][j] is the number of ways to assign the first i indices monotonic with 
// the last element being j in array 1

class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();

        forz(i,0,n){
            forz(j,0,51){
                dp[i][j] = 0;
            }
        }

        forz(i,0,nums[0]+1){
            dp[0][i] = 1;
        }

        forz(i,1,n){
            forz(j,0,nums[i]+1){
                forz(k,0,nums[i-1]+1){
                    int last1 = k;
                    int last2 = nums[i-1]-k;
                    if ((last1 <= j) && last2 >= (nums[i]-j)){
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= mod;
                    }
                }
            }
        }

        ll ans = 0;
        forz(i,0,nums[n-1]+1){
            ans += dp[n-1][i];
            ans %= mod;
        }

        return ans;
    }
};
