#define ll long long

class Solution {

ll M = 1e9+7;
private:
ll pairs(vector<int> &nums, int ind, int prev1, int prev2, vector<vector<vector<int>>> &dp){

    if(ind == nums.size()) return 1;
    if(dp[ind][prev1][prev2] != -1) return dp[ind][prev1][prev2] % M;

    ll i = prev1, ans = 0;
    while(i <= nums[ind]){
        int j = nums[ind] - i;
        if(j <= prev2) {
            ans = (ans + pairs(nums, ind+1, i, j,dp)) % M;
        }
        i++;
    }

    return dp[ind][prev1][prev2] = ans % M;
}
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(55,-1)));
        int ans = pairs(nums, 0, 0, 51, dp) ;
        return ans % M;
    }
};
