class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10 ** 9 + 7
        mv = max(nums)
        odp = [0] * (mv + 1)
        for i in range(nums[0] + 1):
            odp[i] = 1

        for i in range(1, len(nums)):
            #print(odp)
            ndp = [0] * (mv + 1)
            for j in range(nums[i] + 1):
                for k in range(j + 1):
                    if nums[i - 1] - k >= nums[i] - j:
                        ndp[j] = (ndp[j] + odp[k]) % M
            odp = ndp
        #print(odp)
        return sum(odp) % M
