class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        m = len(nums)
        mod = 10**9+7
        tree = []
        for i in range(m):
            new = []
            v = nums[i]
            for j in range(v+1):
                p1,p2 = j,v-j
                if i==0:
                    new.append([p1,p2,1])
                else:
                    sub = 0
#                     end = bisect_left(tree,p1,key= lambda x:x[0])
                    for k in range(len(tree)):
                        b1,b2,count = tree[k]
                        if p1>=b1 and p2<=b2:
                            sub+=count
                    if sub>0:
                        new.append([p1,p2,sub])

            tree = new
        res = 0
        for i in range(len(tree)):
            res+=tree[i][2]
        return res%mod
