class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int mod = (int) (1e9 + 7);
        int max = 51;
        long[][][] dp = new long[n][max][max];
        for (int i = 0; i < n; i++) {
            int t = nums[i];
            long all = 0;
            for (int j = 0; j <= t; j++) {
                int at = t - j;
                if (i == 0) {
                    dp[i][j][at] = 1;
                    continue;
                }
                if (j == 0) {
                    for (int k = 0; k <= j; k++) {
                        for (int l = at; l < max; l++) {
                            all += dp[i-1][k][l];
                            all %= mod;
                        }
                    }
                } else {
                    for (int l = at; l < max; l++) {
                        all += dp[i-1][j][l];
                        all %= mod;
                    }
                    for (int k = 0; k < j; k++) {
                        all += dp[i-1][k][at];
                        all %= mod;
                    }
                }
                dp[i][j][at] = all;
//                for (int k = 0; k <= j; k++) {
//                    for (int l = at; l < max; l++) {
//                        dp[i][j][at] += dp[i-1][k][l];
//                        dp[i][j][at] %= mod;
//                    }
//                }
            }
        }
        long ans = 0;
        for (int i = 0; i < max; i++) {
            for (int j = 0; j < max; j++) {
                ans += dp[n-1][i][j];
                ans %= mod;
            }
        }
        return (int) ans;
    }


}
