class Solution {
    private static final int MOD = 1_000_000_007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][][] dp = new int[n][51][51];
        int[][] prefixSum = new int[51][51];

        for (int a1 = 0; a1 <= nums[0]; a1++) {
            int a2 = nums[0] - a1;
            if (a2 >= 0) {
                dp[0][a1][a2] = 1;
            }
        }

        for (int i = 1; i < n; i++) {
            for (int a1 = 0; a1 <= 50; a1++) {
                for (int a2 = 0; a2 <= 50; a2++) {
                    prefixSum[a1][a2] = dp[i-1][a1][a2];
                    if (a1 > 0) prefixSum[a1][a2] = (prefixSum[a1][a2] + prefixSum[a1-1][a2]) % MOD;
                    if (a2 > 0) prefixSum[a1][a2] = (prefixSum[a1][a2] + prefixSum[a1][a2-1]) % MOD;
                    if (a1 > 0 && a2 > 0) prefixSum[a1][a2] = (prefixSum[a1][a2] - prefixSum[a1-1][a2-1] + MOD) % MOD;
                }
            }

            for (int a1 = 0; a1 <= nums[i]; a1++) {
                int a2 = nums[i] - a1;
                if (a2 >= 0) {
                    dp[i][a1][a2] = prefixSum[a1][50];
                    if (a2 > 0) dp[i][a1][a2] = (dp[i][a1][a2] - prefixSum[a1][a2-1] + MOD) % MOD;
                }
            }
        }

        int result = 0;
        for (int a1 = 0; a1 <= 50; a1++) {
            for (int a2 = 0; a2 <= 50; a2++) {
                result = (result + dp[n-1][a1][a2]) % MOD;
            }
        }

        return result;
    }
}
