#define ll long long
class Solution {
public:
    const ll mod=1e9+7;
    ll func(vector<int>&nums,ll ind,ll i,ll j,vector<vector<vector<ll>>>&dp){
        int n=nums.size();
        if(ind>=n){
            return 1;
        }
        if(dp[ind][i][j]!=-1){
            return dp[ind][i][j];
        }
        ll res=0;
        // as only till 50
        for(ll ii=i;ii<=nums[ind];ii++){
            for(ll jj=j;jj>=0;--jj){
                if(ii+jj==nums[ind]){
                    res=(res+func(nums,ind+1,ii,jj,dp))%mod;
                }
            }
        }
        res%=mod;
        return dp[ind][i][j]=res;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<ll>>> dp(n+1, vector<vector<ll>>(55, vector<ll>(55, -1)));
        ll ans=0;
        ans=func(nums,0,0,nums[0],dp);
        return ans;
    }
};
