MOD = 10**9 + 7


class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        if n == 0:
            return 0

        dp = [[0] * (num + 1) for num in nums]

        for x in range(nums[0] + 1):
            dp[0][x] = 1

        for i in range(1, n):
            prefix_sum = [0] * (nums[i - 1] + 1)
            prefix_sum[0] = dp[i - 1][0]

            for y in range(1, nums[i - 1] + 1):
                prefix_sum[y] = (prefix_sum[y - 1] + dp[i - 1][y]) % MOD

            for x in range(nums[i] + 1):
                max_y = min(nums[i - 1], x)
                max_y = min(max_y, nums[i - 1] - nums[i] + x)
                if max_y >= 0:
                    dp[i][x] = prefix_sum[max_y]

        return sum(dp[n - 1]) % MOD
