class Solution {
public:
    long long int mod=1e9+7;
    long long int f(int k,int i,int j,vector<int>&nums,vector<vector<vector<int>>>&dp){
        if(k>=nums.size()) return 1;
        else{
            if(dp[k][i][j]!=-1){
                return dp[k][i][j];
            }
            else{
                dp[k][i][j]=0;
                for(int x=i;x<=nums[k];x++){
                    for(int y=j;y>=0;y--){
                        if(x+y==nums[k]){
                            dp[k][i][j]=(dp[k][i][j]%mod+f(k+1,x,y,nums,dp)%mod)%mod;
                        }
                    }
                }
            }
            // cout<<i<<" "<<j<<" "<<k<<" "<<dp[k][i][j]<<endl;
            return dp[k][i][j]%mod;
        }
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        long long int sum=f(0,0,nums[0],nums,dp);
        return (int)(sum%mod);
    }
};
