class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> numPairs(nums.size(), vector<int>(51, -1));
        return countOfPairs(0, 50, 0, nums, numPairs);
    }

private:
    int countOfPairs(int prev1, int prev2, int pos, vector<int>& nums, vector<vector<int>>& numPairs) {
        if (pos == nums.size()) return 1;

        if (numPairs[pos][prev1] != -1) return numPairs[pos][prev1];
        numPairs[pos][prev1] = 0;

        for (int curr1 = prev1; curr1 <= nums[pos]; ++curr1) {
            int curr2 = nums[pos] - curr1;
            if (curr2 > prev2) continue;

            numPairs[pos][prev1] += countOfPairs(curr1, curr2, pos + 1, nums, numPairs);

            numPairs[pos][prev1] %= 1000000007;
        }

        return numPairs[pos][prev1];
    }
};
