const int M = 1e9 + 7;
#define it(i, n)    for (int i=0; i<n; i++)
class Solution {
public:
    int dp[2001][51][51];  // Use a 3D array for memoization

    int rec(int level, int v1, int v2, vector<int>& a) {
        if (level >= a.size()) 
            return 1;

        if (dp[level][v1][v2] != -1) 
            return dp[level][v1][v2];

        int pairs = 0;

        it (i, a[level]+1) {
            if (i >= v1 && a[level] - i <= v2) 
                pairs = (pairs + 0LL + rec(level + 1, i, a[level] - i, a)) % M;
        }

        return dp[level][v1][v2] = pairs ;
    }

    int countOfPairs(vector<int>& a) {
        memset(dp, -1, sizeof(dp));  // Reset dp array for each new test case
        return rec(0, 0, 50, a);
    }
};
