class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));
        for (int i = 0; i <= nums[0]; i++) {
            dp[0][i][nums[0] - i] = 1;
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {
                    if (dp[i-1][j][k] > 0) {
                        for (int j_next = j; j_next <= nums[i]; j_next++) {
                            int k_next = nums[i] - j_next;
                            if (k_next <= k) {
                                dp[i][j_next][k_next] = (dp[i][j_next][k_next] + dp[i-1][j][k]) % MOD;
                            }
                        }
                    }
                }
            }
        }
        int result = 0;
        for (int j = 0; j <= 50; j++) {
            for (int k = 0; k <= 50; k++) {
                result = (result + dp[n-1][j][k]) % MOD;
            }
        }

        return result;
    }
};
