class Solution {
public:
    int mod=1e9+7;
    int solve(vector<int> &nums,int j,int prev,int prev1,vector<vector<vector<long long>>> &dp)
    {

        if(j==nums.size())
            return 1;


        if(dp[j][prev][prev1]!=-1)
            return dp[j][prev][prev1];

        long long ans=0;
        for(int i=0;i<=nums[j];i++)
        {

            int p1=i;
            int p2=nums[j]-i;

            if(p1>=prev && p2<=prev1)
            {
                // cout<<p1<<" "<<p2<<" "<<j<<endl;
                int temp=solve(nums,j+1,p1,p2,dp)%mod;
                ans+=temp;
                ans%=mod;
            }
        }

        return dp[j][prev][prev1]=ans%mod;
    }


    int countOfPairs(vector<int>& nums) {

        vector<vector<vector<long long>>> dp(nums.size(),vector<vector<long long>>(51,vector<long long>(51,-1)));
        int ans=solve(nums,0,0,50,dp);
        return ans;
    }
};
